import React, { Component } from 'react';
import Navbar from "./Navbar.js";
import { Link, Redirect, withRouter } from 'react-router-dom';
import "../style/forms.css"
import axios from 'axios';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker-cssmodules.css';
import url from '../config/default'
import "react-datepicker/dist/react-datepicker.css";


class Forms extends Component {

    constructor(props) {
        super(props);

        this.state = {
          activity: []
        };
        // this.handleChange = this.handleChange.bind(this);
        // this.onSubmit = this.onSubmit.bind(this);
      }

      componentDidMount() {
        axios
          .get(`${url.sessionManagement}/get_user_session/${localStorage.getItem('user')}`)
          .then(response => {
            console.log(response.data);


            this.setState({ activity: response.data });
          })
          .catch(e => {
            console.log(e);
          });
      }




    render() {
        return (
            <div>
                <Navbar></Navbar>
                <div className="">
                <div class='container mt-6 mb-6'>
                {this.state.activity.map(activity=> (
                    <form onSubmit={this.handleSubmit}>
                        <div className='row mt-6'>
                            <div className='col-sm-12'>
                                <div className='card'>
                                    <div className='card-header d-flex align-items-center'>
                                        <i className='material-icons mr-2' >Reflectivity Data Extractor</i>

                                    </div>  
                                    <div className='card-body text-center'>
                                    <label for=''>Radar Type</label>
                                        <div className='form-row'>
                                            <div className='col-sm-12'>
                                                <input
                                                    style={{width: "100%"}}
                                                    type="text"
                                                    value={this.state.event_category}
                                                    onChange={this.handleCategory}
                                                    id='event_category'
                                                    placeholder="Enter Your Radar Configuration"
                                                    name="event_category" />
                                            </div>

                                        </div>

                                        <label for=''>Date</label>
                                        <div class='form-group'>
                                        <input
                                                    style={{width: "100%"}}
                                                    type="date"
                                                    value={this.state.date}
                                                    onChange={this.handleDate}
                                                    id='event_date'
                                                    name="event_date" />
                                            </div>

                                        <div class='form-group'>
                                            <label for='event_description'>Description</label>
                                            <textarea className='form-control'
                                                value={this.state.event_description}
                                                onChange={this.handleDescription}
                                                id='event_description'
                                                name='event_description'
                                                placeholder='Enter Small Description'
                                                rows='4'></textarea>
                                        </div>

                                    </div>
                                </div>
                            </div>
                        </div>


                        <div className='form-group d-flex justify-content-center mt-3'>


                        </div>
                    </form>
                          ))};
                </div>
                </div>
            </div>
        );


    }
}

export default Forms;
