import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Register extends Component {
	constructor(props) {
		super(props);

		this.state = {
			name: '',
			mobileNumber: "",
			address: "",
			emailID: '',
			password: '',
			status:''
		};

		this.update = this.update.bind(this);

		this.displayLogin = this.displayLogin.bind(this);
	}

	// http://localhost:8080/rest/user/register

	update(e) {
		let name = e.target.name;
		let value = e.target.value;
		this.setState({
			[name]: value
		});
	}

	displayLogin(e) {
		e.preventDefault();
		// axios request will go
		console.log('You have successfully registered');
		console.log(this.state);

		// axios.post('url/register', {this.state})
		// .then(res => {
		// 	if res.status == '200' {
		// 		alert('sucess')
		// 		redirect to login
		// 	}
		// })
		// .catch(e => {
		// 	console.log(e)
		// })

		this.setState({
			fullname: '',
			email: '',
			password: ''
		});

		const { history } = this.props;

		axios.post('http://localhost:8080/rest/login', user, {headers: {
			'Content-Type': 'application/json',
			'Access-Control-Allow-Origin': '*',
		}})
		  .then(function (response) {

			  if (response.status == "200")
			  {
				console.log("logged in duccesfully");
				history.push('/dashboard')
				// localStorage.setItem(name,this.fullname)
				// return <Redirect to='/dashboard' />

			  }
			console.log(response);
		  })
		  .catch(function (error) {
			console.log("ALLAL",error);
			console.log("data", {user});
		  });
	}



	render() {
		return (
			<div className="container">
			<div className="register">
				<form onSubmit={this.displayLogin}>
					<h2>Register</h2>

					<div className="name">
						<input
							type="text"
							placeholder="Full Name"
							name="fullname"
							value={this.state.fullname}
							onChange={this.update}
						/>
					</div>

					<div className="email">
						<input
							type="text"
							placeholder="Enter your email"
							name="email"
							value={this.state.email}
							onChange={this.update}
						/>
					</div>

					<div className="pasword">
						<input
							type="password"
							placeholder="Password"
							name="password"
							value={this.state.password}
							onChange={this.update}
						/>
					</div>

					<div className="password">
						<input type="password" placeholder="Confirm Password" name="password1" />
					</div>

					<input type="submit" value="Login" />
				</form>

				<Link to="/">Login Here</Link>
			</div>
			</div>
		);
	}
}

export default Register;
