import React, { Component } from 'react';
import { Link, Redirect, withRouter } from 'react-router-dom';
import axios from 'axios';
import request from 'request';
import '../App.css'
import history from '../history';s

export default class Login extends React.Component {
	state = {
		"id": null,
		"name": "Brogrammers",
		"mobileNumber": null,
		"address": null,
		"emailID": null,
		"password": "1234567890",
		"status": null
	  }

	  handleChange = event => {
		this.setState({ userid: event.target.value });
		this.setState({ fullname: event.target.value });
		this.setState({ usergroup: event.target.value });
		this.setState({ emailID: event.target.value });
		this.setState({ mobile: event.target.value });
		this.setState({ password: event.target.value });
	  }

	  handleSubmit = event => {
		event.preventDefault();

		const user = {
		  name: this.state.name,
		  id: this.state.id,
		  mobileNumber:this.state.mobileNumber,
		  address:this.state.address,
		  emailID:this.state.emailID,
		  password:this.state.password,
		  status:this.state.status

		};

		// request
		// 	.post('http://localhost:8080/rest/login')
		// 	.on('response', function(response) {
		// 	console.log(response)
		// 	})

		axios.post('http://localhost:8080/rest/login', user, {headers: {
			'Content-Type': 'application/json',
			'Access-Control-Allow-Origin': '*',
		}})
		  .then(function (response) {
			  console.log(this.props)
			  if (response.status == "200")
			  {
				console.log("logged in duccesfully");
				history.push('/dashboard')
				// return <Redirect to='/dashboard' />

			  }
			console.log(response);
		  })
		  .catch(function (error) {
			console.log("ALLAL",error);
			console.log("data", {user});
		  });

	  }



	render() {
		return (
			<div className="container">
			<div className="login">
				<form onSubmit={this.handleSubmit}>
					<h2>Login</h2>
					<div className="username">
						<input
							type="text"
							placeholder="Username..."

							onChange={this.handleChange}
							name="email"
						/>
					</div>

					<div className="password">
						<input
							type="password"
							placeholder="Password..."

							onChange={this.handleChange}
							name="password"
						/>
					</div>

					<input type="submit" />
				</form>

				<Link to="/register">Create an account</Link>
			</div>
			</div>
		);
	}
}
